mapper.select:
select o.*,u.username as username,u.address as address
from XOder o,XUser u
where  u.id=o.User_id and o.id=#{id}

select o.*,u.username as username,u.address as address
from XOder o,XUser u
where  u.id=o.User_id and o.id=#{id}

 select o.*,u.username as username
from XOder o,XUser u
where  u.id=o.User_id and o.code=#{code}
